/*Setup*/
:root{
  --bodyBg:#E6ED3B;
  --mainBackground:rgba(0,0,0,0.01);
  --favListBg: #fff;
  --resBg: rgba(0,0,0,0.2);
  --secondBackground: #FCF7FC;
  --mainFont: 'Roboto', sans-serif;
  --logoFont: 'Montserrat', sans-serif;
  --buttonBg: #383738;
  --mainColor: #383738;
 }
@import url('https://fonts.googleapis.com/css2?family=Roboto&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Montserrat:wght@600&display=swap');
*{
  box-sizing: border-box;
  letter-spacing: 0.04em;
}
body{
  padding: 0;
  margin: 0;
  height: 100vh;
  background: var(--bodyBg);
  font-family: var(--mainFont);
}

/* Scroll styles */
body::-webkit-scrollbar {
  width: 4px;
}
body::-webkit-scrollbar-track {
  background: #ddd;
}
body::-webkit-scrollbar-thumb {
  background: #444;
  border-radius: 4px;
}
html{
    scroll-behavior: smooth;
}

/*padding standards - demo */

header, main, footer{
  padding: 0.6em 1em;
}
.dailyMenu, .reviews, nav div{
  padding: 0.3em 0.8em;
}

.addReview, .review{
  padding: 0.1em 0.5em;
}

.mainRight, .mainLeft{
  padding: 2em;
}

  /* Main styles */
  
  /* Fav restaurants list styles */

    aside{
     box-sizing: border-box;
     background: var(--favListBg);
     position: fixed;
     z-index: 1;
     height: 100%;
     width: 0;
     top: 0;
     right: -4em;
     transition: 0.5s;
     font-size: 1em;
     padding: 1em 2em;
   }

   .closeFav{
     position: absolute;
     top: 0; 
     right: 0.3em;
     font-size: 2em;
     cursor: pointer;
   }


  /* header styles */
  header{
    height: 15%;
    display: grid;
    grid-template-columns: 1fr 15%;
    font-size: 1.2em;
    font-family: var(--logoFont);
  }
  .siteTitle, .favTrigger{
    display: flex;
    align-items: center;
  }
  .logo{
    height: 2.2em;
    width: auto;
  }
  .logoTitle{
    padding-left: 0.7em;
    font-weight: bold;
    text-align: center;
  }
  .favIcon{
    height: 1.7em;
    width: auto;
    cursor: pointer;
  }
  .favTrigger{
    justify-content: flex-end;
  }


  /* Main search block styles */
  main{
    background: var(--secondBackground);
    height: 78%;
    position: relative;
    display: flex;
    justify-content: center;
    align-items: center;
  }
  

  .formTitle{
    color: var(--mainColor);
    font-size: 1.5em;
    text-transform: uppercase;
    text-align: center;
  }

  .townSearch{
    background: transparent;
    border: none;
    border-bottom: 1px solid black;
    font-size: 1.4em;
    padding: 0.2em 0.4em;
    color: var(--mainColor);
    font-family: var(--mainFont);
    outline: none; 
    display: block;
    margin: 0 auto;
  }

  .townFind{
    border: 2px solid var(--buttonBg);
    padding: 0.3em 0.9em;
    font-size: 1.4em;
    font-family: var(--mainFont);
    font-weight: bold;
    background: var(--mainBackground);
    color: var(--mainColor);
    cursor: pointer;
    outline: none;
    display: block;
    margin: 0 auto;
  }

    button{
    border: 2px solid var(--buttonBg);
    padding: 0.2em 0.8em;
    font-size: 1em;
    font-family: var(--mainFont);
    font-weight: bold;
    background: #FCF7FC;
    color: var(--mainColor);
    cursor: pointer;
    outline: none;
  }

  .imgDec{ 
    height: 3.5em;
    width: auto;
    position: absolute;
    right: 1em;
    bottom: 1em;
  }

  /* List of restaurants styles Edit when API
    Display: none; by default
    display: grid; with js
  */
  nav{
    background: var(--bodyBg);
    display: grid;
    grid-gap: 1em;
    padding: 1em 2em;
  }

  .paginationContainer, .filterRestaurants{
    display: flex;
    justify-content: center;
    align-items: center;
  }

  .restaurantsNavCon{
    display: grid;
    grid-gap: 1em;
  }

  .paginationContainer{
    display: flex;
    margin-left: auto;
    margin-right: auto;
    justify-content: space-between;
    max-width: 650px;
    width: 100%;
  }

  .resDiv span{
    display: block;
  }
  
  .resImg{
    height: 0.9em;
    width: auto;
    position: absolute;
    right: 1em;
    top: 50%;
    transform: translateY(-50%);
  }

  .resDiv{
    box-shadow: -1px 2px 3px 3px rgba(0,0,0,0.35);
    background:#fff;
    width: 100%;
    display: grid;
    cursor: pointer;
    position: relative;
    color: var(--mainColor);
    max-width: 650px;
    margin: 0 auto;
  }
  .resTitle,.restaurantName, .dailyMenuTitle{
    font-weight: bold;
    font-size: 1.1em;
  }
  .resAdr, .restaurantAdress{
    font-size: 0.9em;
  }

  
  .dishCnt{
    display: grid;
    grid-template-columns: 1fr 20%;
  }
  .cost{
    text-align: right;
  }

  .dishCnt span{
    display:block;
  }

  /* Display restaurant styles Edit when API
    Display: none; by default
    Display: block; with js
  */
  article{
    background: var(--secondBackground);
    color: var(--mainColor);
    padding: 2em;
  }
  
  .restaurantContainer{
    padding: 1em;
    margin: 0 auto;
    background: var(--resBg);
    position: relative;
    max-width: 1200px;
    display: grid;
  }

  .restaurantContainer span{
    display: block;
  }

  .restaurantContainer input[type="checkbox"]{
    background: rgb(117, 117, 117);
    position: relative;
    width: 60px;
    height: 30px;
    appearance: none;
    border-radius: 20px;
    outline: none;
    box-shadow: inset 0 0 0 3px rgba(0, 0, 0, 0.15);
    transition: 1s;
  }
  .restaurantContainer input:checked[type="checkbox"]{
    background: rgb(45, 143, 7);
  }
  .restaurantContainer input[type="checkbox"]:before{
    content: " ";
    position: absolute;
    width: 30px;
    height: 30px;
    border-radius: 50%;
    top: 0;
    left: 0;
    background: rgb(117, 117, 117);
    border: 0.2px solid black;
    
    transform: scale(1.1);
    transition:  0.5s;
  }
  .restaurantContainer input:checked[type="checkbox"]:before{
    left:30px;
  }


  .restaurantImg{
    height: 5em;
    width: auto;
  }

  .addFav{
    position: absolute;
    top: 0.8em;
    right: 0.6em;
  }
  .restaurantAvgRating span, .restaurantAvgCost span{
    font-weight: bold;
    
  }
  .addReview{
    border: red solid 2px;
  }
  .addReviewTitle{
    font-weight: bold;
    font-size: 0.9em;
   
  }



  /* Footer styles */
  footer{
    background: var(--mainBackground);
    height: 7%;
    display: flex;
    justify-content: center;
    align-items: center;
    font-weight: bold;
  }


  #reviewCustomInput{
    font-size: 0.7em;
    margin-top: 0.3em;
  }
  #reviewSubmit{
    font-size: 0.8em;
    margin-top: 0.3em;
  }
  input::placeholder{
    color: rgba(0,0,0,0.4);
  }

  /* Media queries */
  @media only screen and (min-width: 700px){
    :root{
      font-size: 20px;
    }
  }
  @media only screen and (min-width: 1080px){
    :root{
      font-size: 22px;
    }
  }
